import React from 'react'
import * as productList from './../Overview/data/productList.json'

const Product = ({match}) => {
    const products = productList.default;

    let product = products.find((product) => product.SKU === match.params.sku);
    const listItems = product.usps.map((usp, index) =>
        <li key={index}>{usp}</li>
    );
    const priceFormat = '€' + parseFloat(product.price) + ',-';
    return (
        <>
            {
                product.name && <h1>{product.name}</h1>
            }

            {
                listItems && <ul>{listItems}</ul>
            }

            {
                product.img.img_url && product.img.img_alt &&
                <img src={product.img.img_url} alt={product.img.img_alt}/>
            }

            {
                product.subtitle && <h3>{product.subtitle}</h3>
            }

            {
                product.description && <p className="description">{product.description}</p>
            }

            {
                priceFormat && <p className="price">{priceFormat}</p>
            }
        </>
    )
}

export default Product
