import React from 'react'

const Card = ({productInfo}) => {

    const listItems = productInfo.usps.map((index) =>
        <li>{index}</li>
    );

    return (
        <div className="Card">
            <h1>{productInfo.name}</h1>
            <span>SKU: {productInfo.SKU}</span>
            <ul>{listItems}</ul>
            <img src={productInfo.img.img_url} title={productInfo.img.img_alt}/>
            <h3>{productInfo.subtitle}</h3>
            <p>{productInfo.description}</p>
            <a href={productInfo.SKU}>Koop Nu</a>
        </div>
    )
}

export default Card
