import React from 'react'
import * as productList from './../Overview/data/productList.json'
const listItems = productInfo.usps.map((index) =>
    <li>{index}</li>
);
const Product = ({match}) => {
    const products = productList.default;

    let product = products.find((product) => product.SKU === match.params.sku);
    return (
        <>
            {
                product.name && <h1>{product.name}</h1>
            }

            {
                listItems && <ul>{listItems}</ul>
            }

            {
                productInfo.img.img_url && productInfo.img.img_alt &&
                <img src={productInfo.img.img_url} alt={productInfo.img.img_alt}/>
            }

            {
                productInfo.subtitle && <h3>{productInfo.subtitle}</h3>
            }

            {
                productInfo.description && <p class="description">{productInfo.description}</p>
            }

            {
                priceFormat && <p class="price">{priceFormat}</p>
            }
        </>
    )
}

export default Product
