import React from 'react'

const Card = ({productInfo}) => {

    const listItems = productInfo.usps.map((index) =>
        <li>{index}</li>
    );

    const priceFormat = '€' + parseFloat(productInfo.price) + ',-';

    return (
        <div className="Card">
            {
                productInfo.name && <h1>{productInfo.name}</h1>
            }

            {
                listItems && <ul>{listItems}</ul>
            }

            {
                productInfo.img.img_url && productInfo.img.img_alt &&
                <img src={productInfo.img.img_url} alt={productInfo.img.img_alt}/>
            }

            {
                productInfo.subtitle && <h3>{productInfo.subtitle}</h3>
            }

            {
                productInfo.description && <p>{productInfo.description}</p>
            }

            {
                priceFormat && <p class="price">{priceFormat}</p>
            }

            <a href={productInfo.SKU}>Buy now</a>
        </div>
    )
}

export default Card
